#import "Friend.h"

@implementation Friend
+ (instancetype)initWithDictionary:(NSDictionary *)dictionary
{
    if(dictionary[@"first_name"] && dictionary[@"last_name"] && dictionary[@"id"]){
        Friend *newFriend = [[Friend alloc] init];
        newFriend.firstName = dictionary[@"first_name"];
        newFriend.lastName = dictionary[@"last_name"];
        newFriend.friendID = dictionary[@"id"];
        NSArray *emojis = [Friend validEmojis];
        newFriend.emoji = emojis[arc4random_uniform([emojis count])];
        return newFriend;
    }
    else{
        return nil;
    }
}

